mapper.select:
-- 게시판 카테고리 전체조회 및 검색기능 SELECT board_category_desc FROM board_category_tb
WHERE LOWER(board_category_desc) LIKE '%'||#{board_category_desc}||'%'
WHERE rownum BETWEEN #{startRow} AND #{endRow}

mapper.delete:

DELETE 
FROM board_category_tb 
WHERE board_category = #{board_category}; 

DELETE 
FROM board_role_tb 
WHERE board_category_no = (
	select board_category_no
	from board_category_tb  
	where board_category = #{board_category}
);

mapper.update:

UPDATE board_role_tb SET board_role_no = #{board_role_no} 
WHERE board_category_no = (
	SELECT board_category_no 
	FROM board_category_tb 
	WHERE board_category = #{board_category} 
)

mapper.select:

SELECT board_category_desc 
FROM board_category_tb 
WHERE LOWER(board_category) = #{board_category}

mapper.insert:

UPDATE board_tb 
SET board_views = board_views + 1 
WHERE board_no = #{board_no}

merge INTO board_tb using dual ON (#{board_no} > 0)
-- 기존 게시글이 있을 때 글 갱신 -- 인증값으로 받아온 유저 아이디를 유저 테이블에서 유저 번호로 조회 후 -- 해당 게시글의 작성자와 같은지 매칭 WHEN matched then UPDATE SET board_title = #{board_title}, board_content = #{board_content}, board_UPDATEdate = sysdate WHERE board_no = #{board_no} AND user_no = ( SELECT user_no FROM user_tb WHERE user_id = #{user_id} }
-- 기존 게시글이 없을 때 글 추가 WHEN not matched then INSERT (board_title, board_content, board_category, user_no) VALUES (#{board_title}, #{board_content}, #{board_category}, #{user_no})
-- 기존 글이 존재하지 않는 경우 이미지 업로드시 필요한 글 번호를 반환 SELECT MAX(board_no) FROM board_tb WHERE board_category = #{board_category}

mapper.delete:

-- 1. 인증값으로 받아온 회원 아이디를 유저 테이블과 매칭해 유저 번호를 불러옴
-- 2. 게시글 테이블의 유저 번호와 조회된 번호가 같지 않으면 0을 반환 (성공시 1을 반환)
DELETE FROM board_tb 
WHERE board_no = #{board_no} 
	AND user_no = ( 
		SELECT user_no 
		FROM user_tb 
		WHERE user_id = #{user_id} 
	)
