mapper.select:
SELECT board_category AS boardCategory, board_category_desc AS boardCategoryDesc FROM ( SELECT rownum rnum, board_category, board_category_desc FROM board_category_tb
WHERE LOWER(board_category_desc) LIKE LOWER('%'||#{boardCategoryDesc}||'%')
) WHERE rnum BETWEEN #{startRow} and #{endRow}

mapper.update:
UPDATE board_tb
SET board_views = board_views + 1
WHERE board_no = #{boardNo}
