mapper.select:

SELECT count(*)
FROM reply_tb
WHERE board_no = #{board_no}


		-- 3차검색, 페이징 처리 
		SELECT reply_no, r.user_no, u.user_chr, reply_content, reply_group, reply_dept, reply_regdate 
		FROM(

			-- 2차검색, 정렬된 값에 순번을 재부여 
		    SELECT rownum rnum, reply_no, user_no, reply_content, reply_group, reply_dept, reply_regdate 
		    FROM(

		    	-- 1차검색, 검색 후 순차 혹은 역순으로 정렬 
		        SELECT reply_no, user_no, reply_content, reply_group, reply_dept, reply_regdate 
		        from reply_tb 
		        WHERE board_no = #{board_no} 



       		 	ORDER BY reply_group asc, reply_no asc
			) 


        		ORDER BY reply_group desc, reply_no desc
			) 
			ORDER BY rownum desc 


				ORDER BY reply_group asc, reply_no asc
			)



		)r INNER JOIN user_tb u 
		ON r.user_no = u.user_no 
		WHERE rnum between #{startRow} and #{endRow}

mapper.delete:

delete from reply_tb 
where reply_no = #{reply_no} 
AND user_no = 
	(
		SELECT user_no 
		FROM user_tb 
		WHERE user_id = #{user_id} 
	)
