mapper.insert:
INSERT INTO user_role_tb
(
	user_no,
	role_no
)
VALUES
(
	#{userNo},
	#{roleNo}
)

mapper.update:
UPDATE
	user_tb
SET
	user_id = #{userId}

		,user_id = #{userId}


		,user_pw = #{userPw}


		,user_chr = #{userChr}


		,user_email = #{userEmail}

	file_name = #{fileName}
WHERE
	user_no = #{userNo}
